  async getRouteFromUITToBaChieu() {
    if (!this.map) return;

    const start: [number, number] = [106.8021, 10.87]; // lng, lat ÄH CNTT
    const end: [number, number] = [106.692247, 10.805065]; // lng, lat Chá»£ BÃ  Chiá»ƒu

    // XÃ³a route cÅ©
    if (this.routeLayer) {
      this.map.removeLayer(this.routeLayer);
    }

    try {
      const url = `https://router.project-osrm.org/route/v1/driving/${start[0]},${start[1]};${end[0]},${end[1]}?overview=full&geometries=geojson`;

      const res = await axios.get(url);
      const route = res.data.routes[0];

      if (!route) {
        console.warn('KhÃ´ng tÃ¬m tháº¥y route');
        return;
      }

      const coords = route.geometry.coordinates.map((c: any) => [c[1], c[0]]); // [lat,lng]

      // Váº½ marker start & end
      marker([start[1], start[0]])
        .addTo(this.map)
        .bindPopup('ğŸ“ ÄH CNTT')
        .openPopup();
      marker([end[1], end[0]]).addTo(this.map).bindPopup('ğŸ›ï¸ Chá»£ BÃ  Chiá»ƒu');

      // Váº½ polyline
      this.routeLayer = polyline(coords, { color: 'blue', weight: 5 }).addTo(
        this.map
      );

      // Pan + zoom Ä‘á»ƒ tháº¥y toÃ n bá»™ tuyáº¿n
      this.map.fitBounds(this.routeLayer.getBounds());

      console.log(
        `ğŸš— QuÃ£ng Ä‘Æ°á»ng: ${(route.distance / 1000).toFixed(
          2
        )} km, Thá»i gian: ${(route.duration / 60).toFixed(1)} phÃºt`
      );
    } catch (err) {
      console.error('âŒ Lá»—i khi láº¥y route:', err);
    }
  }

